package com.xinput.wechat.result;import com.thoughtworks.xstream.annotations.XStreamAlias;/** * @author <a href="mailto:xinput.xx@gmail.com">xinput</a> * @date 2020-10-10 10:18 */public class BaseResult {    @XStreamAlias("return_code")    private String return_code;    @XStreamAlias("return_msg")    private String return_msg;    @XStreamAlias("error_code")    private String error_code;    public String getReturn_code() {        return return_code;    }    public void setReturn_code(String return_code) {        this.return_code = return_code;    }    public String getReturn_msg() {        return return_msg;    }    public void setReturn_msg(String return_msg) {        this.return_msg = return_msg;    }    public String getError_code() {        return error_code;    }    public void setError_code(String error_code) {        this.error_code = error_code;    }}