package com.xinput.wechat.prod;import com.xinput.bleach.util.JsonUtils;import com.xinput.wechat.WechatPayApi;import com.xinput.wechat.enums.BillTypeEnum;import com.xinput.wechat.exception.WechatPayException;import com.xinput.wechat.request.DownloadBillRequest;import com.xinput.wechat.result.WechatPayBillResult;import org.junit.jupiter.api.Test;/** * @author <a href="mailto:xinput.xx@gmail.com">xinput</a> * @date 2020-10-09 18:29 */public class CaseDownloadbill {    @Test    public void downloadbill01() throws WechatPayException {        String result = WechatPayApi.downloadBillContent("20200917");        System.out.println(result);    }    @Test    public void downloadbill02() throws WechatPayException {        String result = WechatPayApi.downloadBillContent("20200917", BillTypeEnum.ALL.getBillType());        System.out.println(result);    }    @Test    public void downloadbill03() throws WechatPayException {        DownloadBillRequest request = new DownloadBillRequest();        request.setBill_date("20200918");        request.setBill_type(BillTypeEnum.SUCCESS.getBillType());        String result = WechatPayApi.downloadBillContent(request);        System.out.println(result);    }    @Test    public void downloadbill04() throws WechatPayException {        WechatPayBillResult result = WechatPayApi.downloadBill("20200919");        System.out.println(JsonUtils.toJsonString(result, true));    }    @Test    public void downloadbill05() throws WechatPayException {        WechatPayBillResult result = WechatPayApi.downloadBill("20200918", BillTypeEnum.ALL.getBillType());        System.out.println(JsonUtils.toJsonString(result, true));    }    @Test    public void downloadbill06() throws WechatPayException {        DownloadBillRequest request = new DownloadBillRequest();        request.setBill_date("20200918");        request.setBill_type(BillTypeEnum.SUCCESS.getBillType());        WechatPayBillResult result = WechatPayApi.downloadBill(request);        System.out.println(JsonUtils.toJsonString(result, true));    }}