package com.xinput.wechatpay.result;import com.fasterxml.jackson.annotation.JsonIgnore;import com.thoughtworks.xstream.annotations.XStreamAlias;/** * 微信支付退款通知 */@XStreamAlias("xml")public class RefundNotifyDto {    /**     * 返回状态码     * 必填: 是     * 类型: String(16)     * 示例值: SUCCESS     * 描述: SUCCESS/FAIL 此字段是通信标识，非交易标识，交易是否成功需要查看trade_state来判断     */    @XStreamAlias("return_code")    private String return_code;    /**     * 返回信息     * 必填: 是     * 类型: String(128)     * 示例值: OK     * 描述: 当return_code为FAIL时返回信息为错误原因 ，例如 签名失败、参数格式校验错误     */    @XStreamAlias("return_msg")    private String return_msg;    /**     * 小程序ID     * 必填: 是     * 类型: String(32)     * 示例值: wx8888888888888888     * 描述: 调用接口提交的小程序ID     */    @JsonIgnore    @XStreamAlias("appid")    private String appid;    /**     * 退款的商户号     * 必填: 是     * 类型: String(32)     * 示例值: 1900000109     * 描述: 调用接口提交的商户号     */    @JsonIgnore    @XStreamAlias("mch_id")    private String mch_id;    /**     * 随机字符串     * 必填: 是     * 类型: String(32)     * 示例值: 5K8264ILTKCH16CQ2502SI8ZNMTM67VS     * 描述: 随机字符串，长度要求在32位以内。推荐 https://pay.weixin.qq.com/wiki/doc/api/wxa/wxa_api.php?chapter=4_3     */    @XStreamAlias("nonce_str")    private String nonce_str;    /**     * 加密信息     * 必填: 是     * 类型: String(1024)     * 示例值: 5K8264ILTKCH16CQ2502SI8ZNMTM67VS     * 描述: 随机字符串，长度要求在32位以内。推荐 https://pay.weixin.qq.com/wiki/doc/api/wxa/wxa_api.php?chapter=4_3     */    @XStreamAlias("req_info")    private String req_info;    public String getReturn_code() {        return return_code;    }    public void setReturn_code(String return_code) {        this.return_code = return_code;    }    public String getReturn_msg() {        return return_msg;    }    public void setReturn_msg(String return_msg) {        this.return_msg = return_msg;    }    public String getAppid() {        return appid;    }    public void setAppid(String appid) {        this.appid = appid;    }    public String getMch_id() {        return mch_id;    }    public void setMch_id(String mch_id) {        this.mch_id = mch_id;    }    public String getNonce_str() {        return nonce_str;    }    public void setNonce_str(String nonce_str) {        this.nonce_str = nonce_str;    }    public String getReq_info() {        return req_info;    }    public void setReq_info(String req_info) {        this.req_info = req_info;    }}