package com.xinput.wechatpay.util;import org.apache.commons.codec.binary.Base64;public class BASE64Utils {    /**     * 进行Base64解密     *     * @param binaryData     * @return     * @throws Exception     */    public static byte[] decodeBase64(String binaryData) {        byte[] result = Base64.decodeBase64(binaryData.getBytes());        return result;    }    /**     * 进行Base64加密     *     * @param binaryData     * @return     * @throws Exception     */    public static byte[] encodeBase64(byte[] binaryData) {        byte[] result = Base64.encodeBase64(binaryData);        return result;    }}