package com.xinput.wechatpay.util;import com.xinput.bleach.util.StringUtils;import com.xinput.wechatpay.exception.WechatPayException;import javax.validation.ConstraintViolation;import javax.validation.Validation;import javax.validation.Validator;import javax.validation.ValidatorFactory;import java.util.Set;/** * @author <a href="mailto:xinput.xx@gmail.com">xinput</a> * @date 2020-09-29 10:53 */public class ValidateUtils {    public static <T> void validate(T t) throws WechatPayException {        if (null == t) {            throw new WechatPayException("参数为空");        }        ValidatorFactory factory = Validation.buildDefaultValidatorFactory();        Validator validator = factory.getValidator();        Set<ConstraintViolation<T>> constraintViolations = validator.validate(t);        for (ConstraintViolation<T> constraintViolation : constraintViolations) {            if (StringUtils.isNotEmpty(constraintViolation.getMessage())) {                throw new WechatPayException(constraintViolation.getMessage());            }        }    }}