package com.xinput.wechatpay;import com.xinput.bleach.util.BuilderUtils;import com.xinput.bleach.util.JsonUtils;import com.xinput.bleach.util.Logs;import com.xinput.wechatpay.enums.AccountTypeEnum;import com.xinput.wechatpay.exception.WechatPayException;import com.xinput.wechatpay.request.DownloadFundflowRequest;import com.xinput.wechatpay.result.WechatFundFlowResult;import org.junit.jupiter.api.Test;import org.slf4j.Logger;/** * 下载资金账单 * * @author <a href="mailto:xinput.xx@gmail.com">xinput</a> * @date 2020-09-30 11:58 */public class Case_DownloaFundflow {    private static final Logger logger = Logs.get();    @Test    public void downloaFundflow() throws WechatPayException {        DownloadFundflowRequest request = BuilderUtils.of(DownloadFundflowRequest::new)                .with(DownloadFundflowRequest::setBill_date, "20200918")                .with(DownloadFundflowRequest::setAccount_type, AccountTypeEnum.BASIC.getAccountType())                .build();        WechatFundFlowResult result = WechatPayApi.downloadFundflow(request);        logger.info(JsonUtils.toJsonString(result, true));    }}