package com.xinput.wechatpay.prod;import com.xinput.bleach.util.JsonUtils;import com.xinput.bleach.util.Logs;import com.xinput.wechatpay.WechatPayApi;import com.xinput.wechatpay.enums.AccountTypeEnum;import com.xinput.wechatpay.exception.WechatPayException;import com.xinput.wechatpay.result.WechatFundFlowResult;import org.junit.jupiter.api.Test;import org.slf4j.Logger;/** * 下载资金账单 */public class CaseDownloadfundflow {    private static final Logger logger = Logs.get();    @Test    public void downloadfundflow01() throws WechatPayException {        String result1 = WechatPayApi.downloadFundflowContent("20201009", AccountTypeEnum.BASIC);        logger.info(result1);        String result2 = WechatPayApi.downloadFundflowContent("20201009", AccountTypeEnum.OPERATION);        logger.info(result2);        String result3 = WechatPayApi.downloadFundflowContent("20201009", AccountTypeEnum.FEES);        logger.info(result3);    }    @Test    public void downloadfundflow02() throws WechatPayException {        String result1 = WechatPayApi.downloadFundflowContent("20201009", AccountTypeEnum.BASIC);        logger.info(result1);        WechatFundFlowResult result = WechatPayApi.downloadFundflow("20201009", AccountTypeEnum.BASIC);        logger.info(JsonUtils.toJsonString(result, true));    }    @Test    public void downloadfundflow03() throws WechatPayException {        String result1 = WechatPayApi.downloadFundflowContent("20201009", AccountTypeEnum.OPERATION);        logger.info(result1);        WechatFundFlowResult result = WechatPayApi.downloadFundflow("20201009", AccountTypeEnum.OPERATION);        logger.info(JsonUtils.toJsonString(result, true));    }}